$(document).ready(function() {


    $('.delete-notice').click(function (d) {
        del_username = $(this).attr('data-del_username');
        notice_id = $(this).attr('data-notice_id');
        // console.log(post_id);

        var currentRequest = null;
        currentRequest = jQuery.ajax({
            type: 'post',
            url: '../../../views/user/notice/notice.php', //Here you will fetch records
            data: {'del_username': del_username, 'notice_id': notice_id, 'ntc_type': 'delete_ntc'}, //Pass $id
            dataType: 'json',
            cache: false,
            context: this,
            beforeSend: function () {
                if (currentRequest != null) {
                    currentRequest.abort();
                }

            },
            success: function (data) {
                console.log(data);
                $('#notice-message' + notice_id).empty();
                $('#notice-message' + notice_id).append("<div class=" + 'alertStyle' + "><button type=" + 'button' + " class=" + 'close' + " data-dismiss=" + 'alert' + "><span>&times;</span><span class=" + 'sr-only' + ">+'Close'+</span></button>" + 'Notice deleted successfully' + "</div>");
                $('.alertStyle').addClass('alert').addClass('alert-success').addClass('alert-styled-left');

            },
            complete: function (response) {

            },
            error: function (e) {
                // Error
            }
        });


    });

});