$(document).ready(function() {


    $('.delete-paper').click(function (d) {
        del_username = $(this).attr('data-del_username');
        paper_journal_id = $(this).attr('data-paper_journal_id');
        // console.log(post_id);

        var currentRequest = null;
        currentRequest = jQuery.ajax({
            type: 'post',
            url: '../../../views/user/paper&journal/paper_journal.php', //Here you will fetch records
            data: {'del_username': del_username, 'paper_journal_id': paper_journal_id, 'pj_type': 'delete_paper'}, //Pass $id
            dataType: 'json',
            cache: false,
            context: this,
            beforeSend: function () {
                if (currentRequest != null) {
                    currentRequest.abort();
                }

            },
            success: function (data) {
                // console.log(data);
                $('#paper-message' + paper_journal_id).empty();
                $('#paper-message' + paper_journal_id).append("<div class=" + 'alertStyle' + "><button type=" + 'button' + " class=" + 'close' + " data-dismiss=" + 'alert' + "><span>&times;</span><span class=" + 'sr-only' + ">+'Close'+</span></button>" + 'Paper deleted successfully' + "</div>");
                $('.alertStyle').addClass('alert').addClass('alert-success').addClass('alert-styled-left');

            },
            complete: function (response) {

            },
            error: function (e) {
                // Error
            }
        });


    });


    $('.delete-journal').click(function (d) {
        del_username = $(this).attr('data-del_username');
        paper_journal_id = $(this).attr('data-paper_journal_id');
        // console.log(post_id);

        var currentRequest = null;
        currentRequest = jQuery.ajax({
            type: 'post',
            url: '../../../views/user/paper&journal/paper_journal.php', //Here you will fetch records
            data: {'del_username': del_username, 'paper_journal_id': paper_journal_id, 'pj_type': 'delete_journal'}, //Pass $id
            dataType: 'json',
            cache: false,
            context: this,
            beforeSend: function () {
                if (currentRequest != null) {
                    currentRequest.abort();
                }

            },
            success: function (data) {
                // console.log(data);
                $('#journal-message' + paper_journal_id).empty();
                $('#journal-message' + paper_journal_id).append("<div class=" + 'alertStyle' + "><button type=" + 'button' + " class=" + 'close' + " data-dismiss=" + 'alert' + "><span>&times;</span><span class=" + 'sr-only' + ">+'Close'+</span></button>" + 'Journal deleted successfully' + "</div>");
                $('.alertStyle').addClass('alert').addClass('alert-success').addClass('alert-styled-left');

            },
            complete: function (response) {

            },
            error: function (e) {
                // Error
            }
        });


    });

});