$(document).ready(function () {

    $('.profile_submit').click(function (prfsub) {
        var options = {
            type: 'post',
            url: '../../../views/user/contents/profile/profile.php',
            dataType: 'json',
            cache: false,
            context: this,
            beforeSend: function () {
                $('#pro_update_message').empty();
            },
            success: function (data) {
                console.log(data);
                if(data['success']){
                    $('#pro_update_message').append("<div class="+'pro_update_success'+"><button type="+'button'+" class="+'close'+" data-dismiss="+'alert'+"><span>&times;</span><span class="+'sr-only'+">"+'Close'+"</span></button>"+data['success']+"</div>");
                    $('.pro_update_success').addClass('alert').addClass('alert-success').addClass('alert-styled-left');
                }
                if(data['failure']){
                    $('#pro_update_message').append("<div class="+'pro_update_failure'+"><button type="+'button'+" class="+'close'+" data-dismiss="+'alert'+"><span>&times;</span><span class="+'sr-only'+">"+'Close'+"</span></button>"+data['failure']+"</div>");
                    $('.pro_update_failure').addClass('alert').addClass('alert-warning').addClass('alert-styled-left');
                }

            },
            complete: function (response) {

            },
            error: function () {


            }

        };

        $("#profile-update").ajaxForm(options);

    });

    $('.account_submit').click(function (accsub) {
        var options = {
            type: 'post',
            url: '../../../views/user/contents/profile/profile.php',
            dataType: 'json',
            cache: false,
            context: this,
            beforeSend: function () {
                $('#acc_pass_update_message').empty();
            },
            success: function (data) {
                console.log(data);
                if(data['success']){
                    $('#acc_pass_update_message').append("<div class="+'acc_update_success'+"><button type="+'button'+" class="+'close'+" data-dismiss="+'alert'+"><span>&times;</span><span class="+'sr-only'+">"+'Close'+"</span></button>"+data['success']+"</div>");
                    $('.acc_update_success').addClass('alert').addClass('alert-success').addClass('alert-styled-left');
                }
                if(data['failure']){
                    $('#acc_pass_update_message').append("<div class="+'acc_update_failure'+"><button type="+'button'+" class="+'close'+" data-dismiss="+'alert'+"><span>&times;</span><span class="+'sr-only'+">"+'Close'+"</span></button>"+data['failure']+"</div>");
                    $('.acc_update_failure').addClass('alert').addClass('alert-warning').addClass('alert-styled-left');
                }

            },
            complete: function (response) {

            },
            error: function () {


            }

        };

        $("#account-update").ajaxForm(options);

    });



});