function searchValidate(){
    var usn_find_user=document.searchForm.usn_find_user.value;
    var status=false;
    if(usn_find_user.length<1){
        document.getElementById("usn_find_user").innerHTML=
            "Enter Username/Name/Varsity ID";
        status=false;
    }else{
        status=true;
    }
    return status;
}
